int currentNumFiringPerCycle = 0;
void checkProductionHasOperator() {
	if (retractOperatorsIndex > 0) {
		int i;
		int j;
		int removeProductionIndexes[NUMBER_OF_PRODUCTIONS];
		int removeIndex = 0;
		for (i = 0; i < productionIndex; i++) {
			if (retractOperatorsContains(1 + getIDFromOperator(productions[i].savedTempAndFunction[6]))) {
				removeProductionIndexes[removeIndex++] = i;
			}
		}
		for (i = 0; i < removeIndex; i++) {
			for (j = removeProductionIndexes[i]; j < productionIndex - 1; j++) {
				productions[j] = productions[j+1];
			}
			productionIndex--;
		}
	}
}

