void checkCondition() {
	bool conditionSatisfied = false;
	if (valueFunction == isNotEqualTo && globalIdentifier != globalValue) {
		conditionSatisfied = true;
	} else if (valueFunction == isGreaterThan && globalIdentifier > globalValue) {
		conditionSatisfied = true;
	} else if (valueFunction == isLessThan && globalIdentifier < globalValue) {
		conditionSatisfied = true;
	}
	if (conditionSatisfied) {
		globalDoesContain = 1;
	} else {
		globalDoesContain = -1;
	}
}

void nextNumBut() {
	int nextIndex = lookAheadArray[lookAheadIndex];
	int nextElement = getNumButNextElement(globalAttribute, nextIndex);
	int i;
	if (nextElement == -1) {
		lookAheadArray[lookAheadIndex] = 0;
		globalDoesContain = -1;
	} else {
		attributeFunction = nextElement;
		lookAheadArray[lookAheadIndex] = nextIndex + 1;
		globalDoesContain = 1;
	}
}

void catchAll() {
	globalDoesContain = -1;
}

int values[NUM_VALUES];
int valuesIndex = 0;
int containsIndex = -1;

void doValuesContain() {
	if (globalValue == nilAnything) {
		if (lookAheadArray[lookAheadIndex] < valuesIndex) {
			globalValue = values[lookAheadArray[lookAheadIndex]];
			lookAheadArray[lookAheadIndex]++;
		} else {
			globalDoesContain = -1;
			lookAheadArray[lookAheadIndex] = 0;
		}
	} else {
		int contains = -1;
		if (globalValue == EMPTY && valuesIndex == 0) {
			contains = 1;
		} else {
			int i;
			for (i = 0; i < valuesIndex; i++) {
				if (values[i] == globalValue) {
					contains = 1;
					i = valuesIndex;
				}
			}
		}
		if (contains == 1) {
			globalDoesContain = 1;
		} else {
			globalDoesContain = -1;
		}
	}
}

void addValue() {
	if (containsIndex == -1) {
		values[valuesIndex] = globalValue;
		if (valueFunction == addFunction) {
			values[valuesIndex] += attributeFunction;
		} else if (valueFunction == subtractFunction) {
			values[valuesIndex] -= attributeFunction;
		}
		valuesIndex++;
	}
	addOperator = false;
}

int getIndexOfValue() {
	int i = 0;
	for (i = 0; i < valuesIndex; i++) {
		if (values[i] == globalValue) {
			return i;
		}
	}
	return -1;
}

void removeValue() {
	while (valuesIndex > 0) {
		valuesIndex--;
		values[valuesIndex] = 0;
	}
	currentNumAttributes--;
	if (currentNumAttributes == 0) {
		removeNestedCheckArray();
		globalIndex++;
		if (globalIndex == nestedCheckIndex) {
			removeOperator = false;
			nestedCheckIndex = 0;
		} else {
			currentNumAttributes = getNumAttributes(nestedCheckArray[globalIndex]);
		}
	}
}

void removeSpecificValue() {
	int i = containsIndex;
	if (i != -1) {
		for (i = containsIndex; i < valuesIndex - 1; i++) {
			values[i] = values[i + 1];
		}
		valuesIndex--;
		values[valuesIndex] = 0;
	}
	 if(isRetracting) {
		int numAttributes = getNumAttributes(globalValue);
		if (numAttributes > 0) {
			addToNestedCheckArray(globalValue);
		}
	}
	addOperator = false;
}

