bool isRequiredAndProhibited() {
	int i = 0;
	while (i < N && required[i] != 0) {
		if (operators[required[i]-1].operator.isProhibited) {
			return true;
		}
		i++;
	}
	return false;
}

void remove(int index, int &ref[N]) {
	int i = index;
	while (i < N-1 && ref[i] != 0) {
		ref[i] = ref[i+1];
		i++;
	}
	ref[i] = 0;
} 

void removeProhibitedFromAcceptable() {
	int i = 0;
	while (i < N && acceptable[i] != 0) {
		if (operators[acceptable[i]-1].operator.isProhibited) {
			remove(i, acceptable);
		} else {
			i++;
		}
	}
}

int variablesContains(int testId) {
	int i = 0;
	while (i < N && acceptable[i] != 0) {
		if (acceptable[i] == testId) {
			return i;
		}
		i++;
	}
	return -1;
}

void removeWorseAndRejectedFromAcceptable() {
	int i = 0;
	int j = 0;
	int k = 0;
	bool temp[N];
	int containID = -1;
	for (k = 0; k < N; k++) {
		temp[k] = false;
	}
	i = 0;
	while (i < N && acceptable[i] != 0) {
		j = 0;
		while (j < N && operators[acceptable[i]-1].better[j] != 0) {
			containID = variablesContains(operators[acceptable[i]-1].better[j]);
			if (!temp[operators[acceptable[i]-1].better[j] - 1] && containID != -1) {
				temp[operators[acceptable[i]-1].better[j] - 1] = true;
			}
			j++;
		}
		if (operators[acceptable[i]-1].operator.isRejected) {
			temp[acceptable[i] - 1] = true;
		}
		i++;
	}
	i = 0;
	while (i < N) {
		if (temp[i]) {
			containID = variablesContains(i+1);
			if (containID != -1) {
				remove(containID, acceptable);
			}
		}
		i++;	
	}}

void removeBest() {
	int i = 0;
	while (i < N && acceptable[i] != 0) {
		if (!operators[acceptable[i]-1].operator.isBest) {
			remove(i, acceptable);
		} else {
			i++;
		}
	}
}

void removeWorst() {
	int i = 0;
	while (i < N && acceptable[i] != 0) {
		if (operators[acceptable[i]-1].operator.isWorst) {
			remove(i, acceptable);
		} else {
			i++;
		}
	}
}

bool areAllIndifferent() {
	int i = 0;
	int j = 0;
	bool temp[N];
	for(i = 0; i < N; i++) {
		temp[i] = false;
	}
	i = 0;
	while (i < N && acceptable[i] != 0) {
		if (operators[acceptable[i]-1].operator.isUnaryIndifferent == 0 && !operators[acceptable[i]-1].operator.hasNumericIndifferent) {
			temp[acceptable[i]-1] = true;
		}
		i++;
	}
	i = 0;
	while (i < N) {
		if (temp[i]) {
			j = 0;
			if (operators[i].binaryIndifferent[0] == 0) {
				return false;
			}
			while(j < N && operators[i].binaryIndifferent[j] != 0) {
				if(variablesContains(operators[i].binaryIndifferent[j]) == -1) {
					return false;
				}
				j++;
			}
		}
		i++;
	}
	return true;
}

int modulus(int num) {
	if (num == 0) {
		return 0;
	} else {
		int d = num / numLeft;
		int m = numLeft*d;
		int r = num-m;
		return r;
	}
}

